/**
 * Adminhtml config system template source
 *
 * @category   Mage
 * @package    Magestore_Abandoned
 * @author      Magento Core Team <core@magentocommerce.com>
 */
class Magestore_Abandoned_Model_System_Config_Email_Template extends Varien_Object
{
    
    public function toOptionArray()
    {
        if(!$collection = Mage::registry('config_system_email_template')) {
            $collection = Mage::getResourceModel('core/email_template_collection')
                ->load();

            Mage::register('config_system_email_template', $collection);
        }

        $options = $collection->toOptionArray();
        
        array_unshift(
            $options,
            array(
                'value'=> 'magestore_auction_completed_admin',
                'label' => 'New bid notice to admin (Default)'
            ),
            array(
                'value'=> '0',
                'label' => 'None'
            )
        );		
		
		return $options;
    }
}
